import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory hook
import './App.css'; // Import your CSS file for styling

const LoginForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const history = useHistory(); // Initialize useHistory hook

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handlePlaceholderClick = (e) => {
    const { name } = e.target;
    setFormData({
      ...formData,
      [name]: '',
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // You can handle form submission logic here
    // Clear form fields after submission
    setFormData({
      username: '',
      password: '',
    });
    // Redirect to the home page
    history.push('/home');
  };

  return (
    <div className="login-form-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            onFocus={handlePlaceholderClick} // Clear the field when placeholder is clicked
            required
            className="form-input"
            placeholder={formData.username ? '' : 'Username'} // Show placeholder only if no value
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            onFocus={handlePlaceholderClick} // Clear the field when placeholder is clicked
            required
            className="form-input"
            placeholder={formData.password ? '' : 'Password'} // Show placeholder only if no value
          />
        </div>
        <button type="submit" className="submit-button">Login</button>
      </form>
    </div>
  );
};

export default LoginForm;
