import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import './App.css';
import SearchIcon from './search.svg';
import MovieCard from './MovieCard';
import About from './pages/about'; // Import the About component

const API_URL = 'http://www.omdbapi.com?apikey=7c50e322';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
 
    useEffect(() => {
        searchMovies('superman');
    }, []);
   
    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`)
        const data = await response.json()
        setMovies(data.Search);
    }  

    const handleKeyPress = (event) => {
        if(event.key==='Enter'){
            searchMovies(searchTerm)
        }
    }
   
    return (
        <BrowserRouter>
            <div className="App">
                <h1>MovieLand</h1>
                <div className="search">
                    <input placeholder="Search for movies" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} onKeyPress={handleKeyPress}/>
                    <img src={SearchIcon} alt="search" onClick={()=> searchMovies(searchTerm)} />
                </div>
                <Routes>
                    <Route path="/about" element={<About />} /> {/* Route for About component */}
                </Routes>
                {
                    movies?.length > 0 ? (
                        <div className="container">
                            {movies.map((movie) => (<MovieCard key={movie.imdbID} movie={movie} />))}
                        </div>
                    ) : (
                        <div className="empty">
                            <h2>No movies found</h2>
                        </div>
                    )
                }
            </div>
        </BrowserRouter> 
    );
}

export default App;
