import { useState } from 'react';
import './App.css';

const App = () =>{
const [counter, setCounter] = useState(1)

const handleDecrement = () => {
 if(counter>0){
  setCounter((prevCount) => prevCount - 1)
 }
}

const handleIncrement = () => {

  setCounter((prevCount) => prevCount + 1)

}


return (
  <div className='App'>
    <button onClick={handleIncrement} className='buttonIn'>+</button>
    <h1>{counter}</h1>
    <button onClick={handleDecrement} className='buttonDe'>-</button>
  </div>

)

}

export default App;
